import { Sidebar } from "../components/Sidebar";
import { UserRanked } from "../components/UserRanked";

export const Ranking = () => {
  const users = [
    {
      name: "Amet Mattis Vulputate",
      rating: "60",
    },
    {
      name: "Amet Mattis Vulputate",
      rating: "50",
    },
    {
      name: "Amet Mattis Vulputate",
      rating: "40",
    },
    {
      name: "Amet Mattis Vulputate",
      rating: "30",
    },
  ];
  // tentativa de rankear de forma automática
  // for (const e in users) {
  //   const a = users[e].rating;
  //   const b = users[e + 1].rating;

  //   users.sort((a, b) => {
  //     if (a.rating > b.rating) {
  //       return -1;
  //     }
  //   });
  // }

  return (
    <div className="flex min-h-screen max-w-screen w-full h-full">
      <Sidebar />

      <div className="w-4/5 flex flex-col">
        <div className="text-3xl font-montserrat font-semibold mt-8 m-8 ml-20">
          Ranking
        </div>

        <div className="flex flex-row ml-24 mr-28 m-2 w-30 place-content-between">
          <p className="font-montserrat">Name</p>
          <p className="font-montserrat">Rating</p>
        </div>

        {users.map((item, index) => {
          return <div>
            <p className="font-montserrat font-semibold w-20 text-center my-auto">{index+1}°</p>
            <UserRanked name={item.name} rating={item.rating} />
            </div>;
        })}
      </div>
    </div>
  );
};
